// char           *achl_p2_last;            /* string 2 last pos       */
   const char     *achl_p2_end;             /* string 2 end            */
   HL_WCHAR       wcl_p2;
   awcl_p1_cur = awcp_p1;                   /* get string 1 UTF-16     */
