             *((char  *) achl_cur_target + 1) = 0;  /* make zero-terminated */
             *((char  *) achl_cur_target + 2) = 0;  /* make zero-terminated */
             *((char  *) achl_cur_target + 3) = 0;  /* make zero-terminated */
