                         inl_s = inl_dgsp_tm*(inl_dgsp_lo-inl_dgsp_lc);  /* adjustment from logical to physical */
                         achl_after += inl_s;
                         if (inl_dgsp_lo<0 || achl_after-achl_cur>achl_end-achl_cur)
                           {achl_after = achl_cur; break;}  /* overflow */
