                           dsl_diggrp_dm = div(inl_emax_1, D_DIGGRPLEN);
                           inl_dgsp_ph++;
#else
                           inl_dgsp_tm = (inl_emax_1)/D_DIGGRPLEN;
                           inl_dgsp_ph = (inl_emax_1)%D_DIGGRPLEN+1;
